mapper.select:
SELECT DATE_FORMAT(WEATHER_DATE,'%Y-%m-%d') AS weatherDate, CONCAT( LEFT(WEATHER_TIME, 2), ' ì‹œ') AS weatherTime, GET_CODE_NM('WEATHER_TYPE',WEATHER_TYPE) AS weatherType, TEMPERATURE AS temperature, HUMIDITY AS humidity, ATMOSPHERIC_PRESSURE AS atmosphericPressure, WIND_SPEED AS windSpeed, WIND_DIRECTION AS windDirection, LATITUDE AS latitude, LONGITUDE AS longitude, CREATED_AT AS createdAt, CREATED_BY AS createdBy FROM HISTORY_WEATHER WHERE 1=1
AND WEATHER_DATE >= #{startDate}
AND WEATHER_DATE <= CONCAT(#{endDate}, ' 23:59:59')
ORDER BY weatherDate DESC, weatherTime DESC LIMIT #{startRow}, #{pageSize}

SELECT COUNT(*) FROM HISTORY_WEATHER WHERE 1=1
AND WEATHER_DATE >= #{startDate}
AND WEATHER_DATE <= CONCAT(#{endDate}, ' 23:59:59')

mapper.insert:
INSERT INTO HISTORY_WEATHER (

    WEATHER_DATE
    , WEATHER_TIME
    , WEATHER_TYPE
    , TEMPERATURE
    , HUMIDITY
    , ATMOSPHERIC_PRESSURE
    , WIND_SPEED
    , WIND_DIRECTION
    , LATITUDE
    , LONGITUDE
    , CREATED_AT
    , CREATED_BY

) VALUES (
    #{weatherDate}
    , #{weatherTime}
    , #{weatherType}
    , #{temperature}
    , #{humidity}
    , #{atmosphericPressure}
    , #{windSpeed}
    , #{windDirection}
    , #{latitude}
    , #{longitude}
    , now()
    , #{createdBy}
)
